class CrossPlatformBridge{constructor(){this.platform=this.detectPlatform(),this.features=this.detectFeatures(),this.initialized=!1,this.init()}detectPlatform(){if("undefined"==typeof window)return"server";const e=navigator.userAgent.toLowerCase();return e.includes("mobile")?"mobile":e.includes("tablet")?"tablet":"desktop"}detectFeatures(){return"undefined"==typeof window?{}:{localStorage:"localStorage"in window,sessionStorage:"sessionStorage"in window,indexedDB:"indexedDB"in window,webWorker:"Worker"in window,serviceWorker:"serviceWorker"in navigator,geolocation:"geolocation"in navigator,notifications:"Notification"in window,webSocket:"WebSocket"in window}}init(){try{console.log("ğŸ” CrossPlatformBridge initializing...",{platform:this.platform,features:this.features}),this.setupPlatformSpecific(),this.initialized=!0,console.log("ğŸ” CrossPlatformBridge initialized successfully")}catch(e){console.error("ğŸ” CrossPlatformBridge initialization error:",e)}}setupPlatformSpecific(){switch(this.platform){case"mobile":this.setupMobile();break;case"tablet":this.setupTablet();break;case"desktop":this.setupDesktop();break;default:console.log("ğŸ” Unknown platform, using default setup")}}setupMobile(){console.log("ğŸ” Setting up for mobile platform")}setupTablet(){console.log("ğŸ” Setting up for tablet platform")}setupDesktop(){console.log("ğŸ” Setting up for desktop platform")}isFeatureAvailable(e){return this.features[e]||!1}getPlatformInfo(){return{platform:this.platform,features:this.features,initialized:this.initialized,userAgent:"undefined"!=typeof navigator?navigator.userAgent:"unknown"}}}"undefined"!=typeof window&&(window.CrossPlatformBridge=CrossPlatformBridge);export{CrossPlatformBridge};