class DiagnosisDataFormat{constructor(){this.formatVersion="1.0.0",this.dataTypes={QUICK:"quick_analysis",OS:"os_analysis",FUTURE:"future_simulation"}}validateFormat(a){try{return a&&"object"==typeof a?{valid:!0,data:a}:{valid:!1,error:"Invalid data object"}}catch(t){return console.error("üîç Format validation error:",t),{valid:!1,error:t.message}}}formatDiagnosis(a,t="QUICK"){try{const r={id:this.generateId(),type:this.dataTypes[t]||this.dataTypes.QUICK,timestamp:(new Date).toISOString(),version:this.formatVersion,data:a};return this.validateFormat(r)}catch(r){return console.error("üîç Format error:",r),{valid:!1,error:r.message}}}generateId(){return`haqei_${Date.now()}_${Math.random().toString(36).substr(2,9)}`}}"undefined"!=typeof window&&(window.DiagnosisDataFormat=DiagnosisDataFormat);