class StorageManager{constructor(){this.prefix="haqei_",this.version="1.0.0",this.storageAvailable=this.checkStorageAvailability()}checkStorageAvailability(){try{const e="__storage_test__";return localStorage.setItem(e,e),localStorage.removeItem(e),!0}catch(e){return console.warn("🔍 Storage not available:",e),!1}}save(e,r){if(!this.storageAvailable)return!1;try{const t=this.prefix+e,a={data:r,timestamp:(new Date).toISOString(),version:this.version};return localStorage.setItem(t,JSON.stringify(a)),console.log(`🔍 Data saved: ${e}`),!0}catch(t){return console.error("🔍 Storage save error:",t),!1}}load(e){if(!this.storageAvailable)return null;try{const r=this.prefix+e,t=localStorage.getItem(r);if(!t)return null;const a=JSON.parse(t);return console.log(`🔍 Data loaded: ${e}`),a.data}catch(r){return console.error("🔍 Storage load error:",r),null}}remove(e){if(!this.storageAvailable)return!1;try{const r=this.prefix+e;return localStorage.removeItem(r),console.log(`🔍 Data removed: ${e}`),!0}catch(r){return console.error("🔍 Storage remove error:",r),!1}}clear(){if(!this.storageAvailable)return!1;try{return Object.keys(localStorage).forEach(e=>{e.startsWith(this.prefix)&&localStorage.removeItem(e)}),console.log("🔍 All HAQEI data cleared"),!0}catch(e){return console.error("🔍 Storage clear error:",e),!1}}}"undefined"!=typeof window&&(window.StorageManager=StorageManager);export{StorageManager};